import React,{useState} from 'react';
import { ImageBackground,TouchableOpacity,StyleSheet, Text, View, TextInput, Alert } from 'react-native';
import { JSHash, CONSTANTS } from "react-native-hash";
import { FileSystem, Constants, Notifications } from 'expo';
import RNFS from "react-native-fs";


export default function App() { 
  var RNFS = require('react-native-fs');
  var path = RNFS.DocumentDirectoryPath ;  
  RNFS.writeFile(path, 'Lorem ipsum dolor sit amet', 'utf8')
  .then((success) => {
    console.log('FILE WRITTEN!');
  })
  .catch((err) => {
    console.log(err.message);
  });

    const checkLength=(EnteredText) => {
      if(EnteredText.length===6) addHash(EnteredText);
     
    
    } 

    const addHash=(EnteredText) =>{
      JSHash(EnteredText, CONSTANTS.HashAlgorithms.sha256)  
      
      .then(hash => FileSystem.writeAsStringAsync('./password.txt', hash))
      
      .catch(e => console.log(e));  
  
    }

  return (
    <View style={styles.container}>
    <TextInput onChangeText={checkLength} style={{borderColor:'red',borderWidth:2,width:'20%',height:'20%',alignItems:'center',justifyContent:'center'}}
    >
    </TextInput>  
    </View>
  );
}

const styles = StyleSheet.create({
 
  image:{
   backgroundColor:"#8A2FE5",
    alignItems:'center',
    justifyContent:'center',
    width:'100%',
    height:'100%'
  },
  input:{
    alignItems:"center",
    justifyContent:'center',
    backgroundColor: 'rgba(138, 200, 229, 0.2)',
    height:'7%',
    width:'100%',
    borderRadius:30,
    fontSize:22,
    color:'white'
    
  },
  container: {
    flex: 1,
   
    alignItems: 'center',
    justifyContent: 'center',
  },
  content:{
    flex:1,
    height:'70%',
    width:'80%',
    alignItems:'center',
    justifyContent:'center'
  },
  button:
  {
    backgroundColor: 'rgba(138, 200, 229, 0.2)',
    marginTop:40,
    height:'7%',
    width:'100%',
    alignItems:'center',
    borderRadius:60,
    justifyContent:'center',
    
  }
});
