import React,{useState} from 'react';
import {CheckBox ,ImageBackground,TouchableOpacity,StyleSheet, Text, View, TextInput, Alert, Button, RecyclerViewBackedScrollView } from 'react-native';


export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
     color:'red',
    };
  }


  render() {
    return (
      <View  style={styles.container}>
        <TouchableOpacity 
        style ={styles.button}
        onPress={()=> styles.button.color= this.state}
        
        ><Text>PRESSME</Text></TouchableOpacity>
      </View>
    );
  }
}

const styles=StyleSheet.create({
  container:{
    justifyContent:'center',
    alignItems:'center',
    flex:1,
  },
  button:{
    width:100,
    height:30,
    backgroundColor:'red'
  }
});
